import React, { Component } from "react";

const q_a = [

	{
		'question': 'aoisjdaiosjd?',
		'answer': 'asdasiojd',
	}
]
class Questions_Answers extends  React.Component {

	// constructor
	constructor(props) {

		// constructur of parent
		super(props);

		// initial states
		this.state = {

			// flag of get data from server
			get_questions_answers: false,
			// list of questions_answers:
			// [ {question: 'aiusdj', answer: 'aisojdaisdj'} ]
			questions_anwers: null,

		}

	}

	// life cycle component
	componentDidMount(){

		// get request for get data
        axios.get('http://192.168.1.9:4000/questions_anwers')

        	// if ok
            .then(response => {

            	// get data from API
            	var loteos = response.data;

            	// add location item for map location impleemntation
            	loteos.map( function(currentValue, index, arr) {

            		// add location item in required map format
            		currentValue['location'] = [currentValue.latitude, currentValue.longitude];

            	});

            	// update state
                this.setState({ 

                	// flag of getting data from API
                	get_loteos: true, 

                	// update loteos
                	loteos: loteos,

                	num_loteos: loteos.length,

                });

            })

            // if error
            .catch(function (error){

            	// dislpay error in console
                console.log(error);

            });

	}

	// render method
	render(){

		return(

			<div>

				<h2>

					Preguntas frecuentes sobre bienes ra√≠ces

				</h2>

					{this.state.get_questions_answers 

						? 

					        this.state.questions_anwers.map( (q_a, idx) => 

					        	<div>

					        		<p>

					        			{q_a.question}

					        		</p>

					        		<p>

					        			{q_a.answer}

					        		</p>


					        	</div>

					        )


				    	:

				    		<div> Loading </div>

			    	}

			</div>
		);

	}

}

export default Questions_Answers;
